-- √âtape 1 : Ajouter une colonne "slug" dans la table "recipes"
ALTER TABLE recipes ADD COLUMN slug VARCHAR(255) NOT NULL;

-- √âtape 2 : G√©n√©rer des slugs pour les recettes existantes
UPDATE recipes
SET slug = LOWER(REPLACE(title, ' ', '-'));

-- Optionnel : Si vous souhaitez √©liminer les caract√®res sp√©ciaux des slugs
-- Vous pouvez adapter cette commande selon le support de REGEXP_REPLACE dans votre version de MySQL
-- UPDATE recipes
-- SET slug = LOWER(REGEXP_REPLACE(title, '[^a-zA-Z0-9]+', '-'));

-- √âtape 3 : Ajouter une contrainte d'unicit√© pour la colonne "slug"
ALTER TABLE recipes ADD CONSTRAINT unique_slug UNIQUE (slug);


SELECT * FROM recipes WHERE published = 1;

CREATE TABLE recipe_searches (
    id INT AUTO_INCREMENT PRIMARY KEY,
    recipe_id INT NOT NULL UNIQUE,
    search_count INT DEFAULT 0,
    last_searched TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
    FOREIGN KEY (recipe_id) REFERENCES recipes(id) ON DELETE CASCADE
);

ALTER TABLE recipes MODIFY COLUMN ingredients LONGTEXT;
ALTER TABLE recipes MODIFY COLUMN instructions LONGTEXT;

